import ContactItem from '../ContactItem/ContactItem';
import List from '../../list';
import React, { useState } from 'react';

// var result = [{}];

// for (var i in List) {
//     result.push([List[i]]);
// }
function ContactList() {
    const [choosenList, set] = useState(List);
    console.log(choosenList);

    const sortName = () => {
        const byYearDown = List.sort(sortByName);

        setList(byYearDown);
        console.log(byYearDown);
    };

    function sortByName(a, b) {
        if (a.name < b.name) {
            return -1;
        }
        if (a.name > b.name) {
            return 1;
        }
        return 0;
    }

    const sortPop = () => {
        const byYearDown = List.sort(sortbyPop);

        setList(byYearDown);
        console.log(byYearDown);
    };

    function sortbyPop(a, b) {
        if (a.popularity < b.popularity) {
            return -1;
        }
        if (a.popularity > b.popularity) {
            return 1;
        }
        return 0;
    }
    return (
        <section>
            <div className='buttonDiv'><button onClick={sortName}>Sort ny Name</button><button onClick={sortPop}>Sort by Popularity</button></div>
            <div className='header'>
                <h1>Picture</h1>
                <h1>Name</h1>
                <h1>Popularity</h1>
            </div>
            {choosenList.map((item) => {
                return (<ContactItem name={item.name} url={item.pictureUrl} pop={item.popularity} />
                );
            })}
        </section>

    );
}

export default ContactList;
