import './MovieItem.css';
import React, { useState } from 'react';


function MovieItem(props) {
    const [movies, setmovies] = useState(props);

    let sortByDataAc = () => {
        // erstelle eine Kopie eines Arrays
        const movieCopy = [...movies];
        // sortiere nach der Wichtigkeit
        movieCopy.sort((a, b) => a.name.important - b.name.important);

        // Ersetze den state durch den sortierten Array
        setmovies(movieCopy);

    };

    return (<div>
        {
            props.movies.map((movie) => {
                // Ein Key ist ein Einzigartiger Wert, z.B eine id
                return (<div className="Card">
                    <p>{movie.title}</p>s
                    <p>{movie.year}</p>
                    <p>{movie.director}</p>
                    <p>{movie.duration}</p>
                    <p className="genre">{movie.genre}</p>
                    <p>{movie.rate}</p>
                </div>);
            })}
    </div>);
}

export default MovieItem;
