import './MovieItem.css';
import React, { useState } from 'react';


function MovieItem(props) {
    const [movies, setmovies] = useState(props.movies);
    const [checkItem, setCheckItem] = useState("");
    const [mathvar, setmathvar] = useState(+1);

    let sortByDataAc = () => {
        const movieCopy = [...props.movies];
        movieCopy.sort(compare);
        setmovies(movieCopy);
        setCheckItem("date");
        setmathvar(+1);

    };

    let sortByDataDc = () => {
        const movieCopy = [...props.movies];
        movieCopy.sort(compare);
        setmovies(movieCopy);
        setCheckItem("date");
        setmathvar(-1);
    };

    function compare(a, b) {
        if (a.checkItem < b.checkItem) {
            return mathvar * (-1);
        }
        if (a.checkItem > b.checkItem) {
            return mathvar;
        }
        return 0;
    }

    return (<div>
        <button onClick={sortByDataAc}>Sort by Dae Ascending</button>
        <button>Sort by Date Descending</button>
        {
            movies.map((movie) => {
                // Ein Key ist ein Einzigartiger Wert, z.B eine id
                return (<div className="Card">
                    <p>{movie.title}</p>s
                    <p>{movie.year}</p>
                    <p>{movie.director}</p>
                    <p>{movie.duration}</p>
                    <p className="genre">{movie.genre}</p>
                    <p>{movie.rate}</p>
                </div>);
            })}
    </div>);
}

export default MovieItem;
