import React, { useState } from 'react';



function FAQ() {
    const [showanswer, setanswer] = useState("none");

    function activeAnswer() {
        showanswer === "none" ? setanswer("inline-block") : setanswer("none");
    }
    return (
        <div className="wrapperDiv">
            <div><span>Why is React Great?</span><button className="button">+</button></div>
            <div className="answer" style={{ display: showanswer }}>Krasse Lernkurve alla</div>
        </div>
    );
}

export default FAQ;
